import './App.css';
import React from 'react';
function Square(props){
	return (
		<button className="square" onClick={props.onClick}>
			{props.value}
		</button>
	);
}
function calculateWinner(squares){
	const lines = [
		[0,1,2],
		[3,4,5],
		[6,7,8],
		[0,3,6],
		[1,4,7],
		[2,5,8],
		[0,4,8],
		[2,4,6],
	];
	for (let i = 0; i < lines.length; i++){
		const [a,b,c] = lines[i];
		if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
			return [squares[a], 1];
		}
	}
	return [null, null];
}
class Board extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			boardSize: 3,
		};
	}
	renderSquare(i) {
		return (<Square 
			value={this.props.squares[i]}
			onClick={() => this.props.onClick(i)}
		/>);
	}
	renderRow(rowNumber){
		const colNumbers = Array.from(Array(this.state.boardSize).keys());
		const squares = colNumbers.map((colNumber) =>
			this.renderSquare(rowNumber*this.state.boardSize + colNumber)
		);


		return(<div className="board-row">{squares}</div>);

	}
	render(){
		const numbers = Array.from(Array(this.state.boardSize).keys());
		const rows = numbers.map((number) =>
			this.renderRow(number)
		);
		return(
			<div>{rows}</div>
		)	
	}

}

class Game extends React.Component{
	constructor(props) {
		super(props);
		this.state = {
			history: [{
				squares: Array(9).fill(null),
			}],
			xIsNext: true,
			stepNumber: 0,
			displayMovesDescending: true, 
		};
	}
	handleClick(i){
		const history = this.state.history.slice(0, this.state.stepNumber+1);
		const current = history[history.length-1];
		const squares = current.squares.slice();
		if (calculateWinner(squares) || squares[i]) {
			return;
		}
		squares[i] = this.state.xIsNext ? 'X': 'O';
		this.setState({
			history: history.concat([{
				squares: squares,
			}]),
			xIsNext: !this.state.xIsNext,
			stepNumber: history.length,
		});
		console.log(this.state);
	}
	jumpTo(step){
		this.setState({
			stepNumber: step,
			xIsNext: (step % 2) === 0,
		});
	}
	getColAndRow(move){
		const prev = this.state.history[move-1].squares;
		const current = this.state.history[move].squares;
		for(var i = 0; i < current.length; i++){
			if (prev[i] != current[i]){
				return [(i%3) + 1, Math.floor(i/3)+1]
			}
		}
	}
	render(){
		const history = this.state.history;
		const current = history[this.state.stepNumber];
		const winnerResult = calculateWinner(current.squares);
		const winner = winnerResult[0];
		console.log(winner);
		//const winner = calculateWinner(current.squares);
		const moves = history.map((step, move) => {
			const desc = move ?
				'Go to move #' + move + ": " + this.getColAndRow(move):
				'Go to game start';
			if (move == this.state.stepNumber){
				return (
					<li ref = {move}>
						<button onClick={() => this.jumpTo(move)}><b>{desc}</b></button>
					</li>
				);
			}else{
				return(
					<li key = {move}>
						<button onClick={() => this.jumpTo(move)}>{desc}</button>
					</li>

				);
			}
		});
		let status;
		if (winner){
			status = 'Winner: ' + winner;
		}
		else {
			status = "Next player: " + (this.state.xIsNext? 'X': 'O');
		}

		return(
			<div className="game">
				<div className = "game-board">
				<Board 
					squares={current.squares}
					onClick={(i) => this.handleClick(i)}
				/>
				</div>
				<div className = "game-info">
					<div>{status}</div>
					<button onClick={() => this.state.displayMovesDescending = !this.state.displayMovesDescending}>Sort moves ascending or descending</button>
					<ol>
						{this.state.displayMovesDescending ? moves : moves.reverse()}
					</ol>
				</div>
			</div>
		);
	}
}

function App() {
  return (
  	<Game />
  );
}

export default App;
