import './App.css';
import React from 'react';
import r_b from "./img/r.svg";
import n_b from "./img/n.svg";
import b_b from "./img/b.svg";
import q_b from "./img/q.svg";
import k_b from "./img/k.svg";
import p_b from "./img/p.svg";
import r_w from "./img/R.svg";
import n_w from "./img/N.svg";
import b_w from "./img/B.svg";
import q_w from "./img/Q.svg";
import k_w from "./img/K.svg";
import p_w from "./img/P.svg";

function Square(props){
	var classes = "square";
	((Math.floor(props.id/8) + (props.id % 8)) % 2 != 0) ? classes += " black" : classes += " white"; 
	(props.isHighlighted) ? classes += " highlighted" : classes += "";
	let imgMap = new Map();
	imgMap.set('r', r_b);
	imgMap.set('n', n_b);
	imgMap.set('b', b_b);
	imgMap.set('q', q_b);
	imgMap.set('k', k_b);
	imgMap.set('p', p_b);
	imgMap.set('R', r_w);
	imgMap.set('N', n_w);
	imgMap.set('B', b_w);
	imgMap.set('Q', q_w);
	imgMap.set('K', k_w);
	imgMap.set('P', p_w);
	let imgsrc;
	(props.piece != '') ? imgsrc = imgMap.get(props.piece) : imgsrc = '';
	//imgsrc = require("./logo.svg");
	return (
		<button className={classes} onClick={props.onClick}>
			<img className="piece" src={imgsrc}></img>
		</button>
	);
}
function getBoardFromFEN(FEN){
        var map = [];
        for (var i = 0; i<64; i++){
                map.push('');
        }
        var id = 56;
        const pieces = new Set(['r', 'n', 'b', 'q', 'k', 'p', 'R', 'N', 'B', 'Q', 'K', 'P']);
        var i = 0;
        while (FEN.charAt(i) != ' '){
                var ch = FEN.charAt(i)
                if (pieces.has(ch)){
                        map[id] = ch;
                        id += 1
                }else if(ch == '/'.charAt(0)){
                        id -= 16;
                }else if(ch >= '0'.charAt(0) && ch <= '9'.charAt(0)){
                        id += Number(ch);
                }
                i++;
        }
        return map;
}
function calculateWinner(squares){
	return null;
	//can also return "draw"
}
class Board extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			boardSize: 8,
		};
	}
	renderSquare(i) {
		return (<Square 
			isHighlighted={this.props.highlighted.has(i)}
			id={i}
			piece={this.props.squares[i]}
			onClick={() => this.props.onClick(i)}
		/>);
	}
	renderRow(rowNumber){
		const colNumbers = Array.from(Array(this.state.boardSize).keys());
		const squares = colNumbers.map((colNumber) =>
			this.renderSquare(rowNumber*this.state.boardSize + colNumber)
		);


		return(<div className="board-row">{squares}</div>);

	}
	render(){
		const numbers = Array.from(Array(this.state.boardSize).keys());
		const rows = numbers.map((number) =>
			this.renderRow(number)
		);
		return(
			<div>{rows}</div>
		)	
	}

}

class Game extends React.Component{
	constructor(props) {
		super(props);
		this.state = {
			history: [{
				FEN: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1",
				squares: getBoardFromFEN("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"),
				whiteShortCastle: true,
				whiteLongCastle: true,
				blackShortCastle: true,
				blackLongCastle: true,
				enPassantTarget: null,
			}],
			whiteTurn: true,
			stepNumber: 0,
			selectedPiece: null,
			selectedID: null,
		};
	}
	getLegalMoves(id, piece){
		return [...Array(64).keys()]
	}
	handleClick(i){
		const history = this.state.history.slice(0, this.state.stepNumber+1);
		const current = history[history.length-1];
		const squares = current.squares.slice();
		if (calculateWinner(squares)) {
			return;
		}
		squares[i] = this.state.xIsNext ? 'X': 'O';

		this.setState({
			history: history.concat([{
				squares: squares,
			}]),
			whiteIsNext: !this.state.whiteTurn,
			stepNumber: history.length,
		});
	}
	jumpTo(step){
		this.setState({
			stepNumber: step,
			whiteTurn: (step % 2) === 0,
		});
	}
	render(){
		const history = this.state.history;
		const current = history[this.state.stepNumber];
		const winner = calculateWinner(current.squares);
		const moves = history.map((step, move) => {
			const desc = move ?
				'Go to move #' + move:
				'Go to game start';
			if (move == this.state.stepNumber){
				return (
					<li ref = {move}>
						<button onClick={() => this.jumpTo(move)}><b>{desc}</b></button>
					</li>
				);
			}else{
				return(
					<li key = {move}>
						<button onClick={() => this.jumpTo(move)}>{desc}</button>
					</li>

				);
			}
		});
		let status;
		if (winner == "draw"){
			status = "Draw";
		}
		else if (winner){
			status = 'Winner: ' + winner;
		}
		else {
			status = (this.state.whiteTurn ? 'White': 'Black') + "'s turn";
		}

		return(
			<div className="game">
				<div className = "game-board">
				<Board 
					squares={current.squares}
					highlighted={this.getLegalMoves(this.selectedID, this.seledtedPiece)}
					onClick={(i) => this.handleClick(i)}
				/>
				</div>
				<div className = "game-info">
					<div>{status}</div>
					<ol>
						{moves}
					</ol>
				</div>
			</div>
		);
	}
}

function App() {
  return (
  	<Game />
  );
}

export default App;
