import './App.css';
import React from 'react';
import r_b from "./img/r.svg";
import n_b from "./img/n.svg";
import b_b from "./img/b.svg";
import q_b from "./img/q.svg";
import k_b from "./img/k.svg";
import p_b from "./img/p.svg";
import r_w from "./img/R.svg";
import n_w from "./img/N.svg";
import b_w from "./img/B.svg";
import q_w from "./img/Q.svg";
import k_w from "./img/K.svg";
import p_w from "./img/P.svg";

function legalMovesCheckSide(currentSide, squareSide){
	let add;
	let stop;
	console.log(currentSide, squareSide);
	if (squareSide == 'empty'){
		add = true;
		stop = false;
	}else if(squareSide == currentSide){
		add = false;
		stop = true;
	}else{
		add = true;
		stop = true;
	}
	return [add, stop]
}
function Square(props){
	var classes = "square";
	((Math.floor(props.id/8) + (props.id % 8)) % 2 != 0) ? classes += " black" : classes += " white"; 
	(props.isHighlighted) ? classes += " highlighted" : classes += "";
	let imgMap = new Map();
	imgMap.set('r', r_b);
	imgMap.set('n', n_b);
	imgMap.set('b', b_b);
	imgMap.set('q', q_b);
	imgMap.set('k', k_b);
	imgMap.set('p', p_b);
	imgMap.set('R', r_w);
	imgMap.set('N', n_w);
	imgMap.set('B', b_w);
	imgMap.set('Q', q_w);
	imgMap.set('K', k_w);
	imgMap.set('P', p_w);
	let imgsrc;
	(props.piece != '') ? imgsrc = imgMap.get(props.piece) : imgsrc = '';
	//imgsrc = require("./logo.svg");
	return (
		<button className={classes} onClick={props.onClick}>
			<img className="piece" src={imgsrc}></img>
			{props.id}
		</button>
	);
}
function getSideFromPiece(piece){
	if (new Set(['r', 'n', 'b', 'q', 'k', 'p']).has(piece)){
		return 'black'
	}else if (new Set(['R', 'N', 'B', 'Q', 'K', 'P']).has(piece)){
		return 'white'
	}else{
		return 'empty'
	}
}
function getBoardFromFEN(FEN){
        var map = [];
        for (var i = 0; i<64; i++){
                map.push('');
        }
        var id = 0;
        const pieces = new Set(['r', 'n', 'b', 'q', 'k', 'p', 'R', 'N', 'B', 'Q', 'K', 'P']);
        var i = 0;
        while (FEN.charAt(i) != ' '){
		var ch = FEN.charAt(i)
                console.log(i, id, ch);
                if (pieces.has(ch)){
                        map[id] = ch;
                        id += 1;
                }else if(ch >= '0'.charAt(0) && ch <= '9'.charAt(0)){
                        id += Number(ch);
                }
                i++;
        }
        return map;
}
function calculateWinner(squares){
	return null;
	//can also return "draw"
}
class Board extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			boardSize: 8,
		};
	}
	renderSquare(i) {
		return (<Square 
			isHighlighted={this.props.highlighted.has(i)}
			id={i}
			piece={this.props.squares[i]}
			onClick={() => this.props.onClick(i)}
		/>);
	}
	renderRow(rowNumber){
		const colNumbers = Array.from(Array(this.state.boardSize).keys());
		const squares = colNumbers.map((colNumber) =>
			this.renderSquare(rowNumber*this.state.boardSize + colNumber)
		);


		return(<div className="board-row">{squares}</div>);

	}
	render(){
		const numbers = Array.from(Array(this.state.boardSize).keys());
		const rows = numbers.map((number) =>
			this.renderRow(number)
		);
		return(
			<div>{rows}</div>
		)	
	}

}

class Game extends React.Component{
	constructor(props) {
		super(props);
		this.state = {
			history: [{
				FEN: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1",
				squares: getBoardFromFEN("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"),
				whiteShortCastle: true,
				whiteLongCastle: true,
				blackShortCastle: true,
				blackLongCastle: true,
				enPassantTarget: null,
			}],
			whiteTurn: true,
			stepNumber: 0,
			selectedPiece: null,
			selectedID: null,
		};
	}
	getRookMoves(id){
		const squares = this.state.history[this.state.stepNumber].squares;
		let currentSide;
		(squares[id] == 'R') ? currentSide = 'white' : currentSide = 'black';
		let add;
		let stop;
		var legalMoves = new Set();
		var pointer = id + 8;
		//down verticle check
		console.log('starting down check');
		while (pointer < 64){
			[add, stop] = legalMovesCheckSide(currentSide, getSideFromPiece(squares[pointer]));	
			if (add) legalMoves.add(pointer);
			if (stop) break;
			pointer += 8;
		}
		//up verticle check
		pointer = id - 8;
		while (pointer > 0){
			[add, stop] = legalMovesCheckSide(currentSide, getSideFromPiece(squares[pointer]));	
			if (add) legalMoves.add(pointer);
			if (stop) break;
			pointer -= 8;
		}
		//right check
		pointer = id + 1
		const row = Math.floor(id/8)+1
		while(pointer < row*8){
			[add, stop] = legalMovesCheckSide(currentSide, getSideFromPiece(squares[pointer]));	
			if (add) legalMoves.add(pointer);
			if (stop) break;
			pointer += 1;

		}
		//left check
		pointer = id-1
		while(pointer > (row-1)*8-1){
			[add, stop] = legalMovesCheckSide(currentSide, getSideFromPiece(squares[pointer]));	
			if (add) legalMoves.add(pointer);
			if (stop) break;
			pointer -= 1;

		}
		return legalMoves
	}
	getBishopMoves(id){
		const squares = this.state.history[this.state.stepNumber].squares;
		const boundaries = new Set([0,1,2,3,4,5,6,7,8,15,23,31,39,47,55,63,62,61,60,59,58,57,56,48,40,32,24,16,8])
		let currentSide;
		(squares[id] == 'R') ? currentSide = 'white' : currentSide = 'black';
		let add;
		let stop;
		var legalMoves = new Set();
		var pointer = id + 9;
		//down-right check
		console.log('starting down-right check');
		while (pointer < 64){
			[add, stop] = legalMovesCheckSide(currentSide, getSideFromPiece(squares[pointer]));	
			if (legalMoves.has(pointer)) [add, stop] = [true, true];
			console.log(pointer, add, stop);
			if (add) legalMoves.add(pointer);
			if (stop) break;
			pointer += 9;
		}

		return legalMoves
	}
	getQueenMoves(id){
		return new Set([...this.getRookMoves(id)], [...this.getBishopMoves(id)]);
	}
	getKnightMoves(id){
		return new Set([...Array(64).keys()])
	}
	getKingMoves(id){
		return new Set([...Array(64).keys()])
	}
	getPawnMoves(id){
		return new Set([...Array(64).keys()])
	}
	getLegalMoves(id, piece){
		console.log("ID and piece from get legal moves", id, piece);
		if (id != null && piece != ''){
			if (new Set(['r', 'R']).has(piece)){
				return this.getRookMoves(id)
			}else if (new Set(['b', 'B']).has(piece)){
				return this.getBishopMoves(id)
			}else if (new Set(['n', 'N']).has(piece)){
				return this.getKnightMoves(id)
			}else if (new Set(['q', 'Q']).has(piece)){
				return this.getQueenMoves(id)
			}else if (new Set(['k', 'K']).has(piece)){
				return this.getKingMoves(id)
			}else if (new Set(['p', 'P']).has(piece)){
				return this.getPawnMoves(id)
			}else{
				console.log("Something has gone wrong with get legal moves function")
				return new Set();
			}

		}else{
			return new Set()
		}
	}
	handleClick(i){
		const history = this.state.history.slice(0, this.state.stepNumber+1);
		const current = history[history.length-1];
		var squares = current.squares.slice();
		const selectedPiece = this.state.selectedPiece;
		const selectedID = this.state.selectedID;
		console.log("selected piece and id for handle click", selectedPiece, selectedID);
		if (calculateWinner(squares)) {
			return;
		}else if(selectedPiece && selectedID != null){
			//if i is a legal move
			if (this.getLegalMoves(selectedID, selectedPiece).has(i)){
				squares[selectedID] = ""
				squares[i] = selectedPiece
				this.setState({
					history: history.concat([{
						squares: squares,
					}]),
					whiteIsNext: !this.state.whiteTurn,
					stepNumber: history.length,
					selectedPiece: null,
					selectedID: null,
				});

			}

		}
		else{
			this.setState({
				selectedPiece: squares[i],
				selectedID: i,
			});
		}

	}
	jumpTo(step){
		this.setState({
			stepNumber: step,
			whiteTurn: (step % 2) === 0,
		});
	}
	render(){
		const history = this.state.history;
		const current = history[this.state.stepNumber];
		const winner = calculateWinner(current.squares);
		const moves = history.map((step, move) => {
			const desc = move ?
				'Go to move #' + move:
				'Go to game start';
			if (move == this.state.stepNumber){
				return (
					<li ref = {move}>
						<button onClick={() => this.jumpTo(move)}><b>{desc}</b></button>
					</li>
				);
			}else{
				return(
					<li key = {move}>
						<button onClick={() => this.jumpTo(move)}>{desc}</button>
					</li>

				);
			}
		});
		let status;
		if (winner == "draw"){
			status = "Draw";
		}
		else if (winner){
			status = 'Winner: ' + winner;
		}
		else {
			status = (this.state.whiteTurn ? 'White': 'Black') + "'s turn";
		}
		return(
			<div className="game">
				<div className = "game-board">
				<Board 
					squares={current.squares}
					highlighted={this.getLegalMoves(this.state.selectedID, this.state.selectedPiece)}
					onClick={(i) => this.handleClick(i)}
				/>
				</div>
				<div className = "game-info">
					<div>{status}</div>
					<ol>
						{moves}
					</ol>
				</div>
			</div>
		);
	}
}

function App() {
  return (
  	<Game />
  );
}

export default App;
